/*! ========================================================================
 * Plugins used: owl carousel
 * ======================================================================== */
$(function () {
    // Owl carousel
    // ================================
    $("#gallery-post").owlCarousel({
        lazyLoad: true,
        slideSpeed: 300,
        paginationSpeed: 400,
        singleItem: true,
        autoPlay: true,
        stopOnHover: true,
        navigation: true,
        pagination: false
    });

    
});